	public String devuelveDecimal(String formato,String entrada,String alineado,String caracterRelleno,String largoSalida){
		String salida="";
		int contador=0;
		int largo = entrada.length();
		String caracter="";
		boolean existe = false;
		while(contador<largo){
			caracter= entrada.substring(contador, contador+1);
			if(caracter.equalsIgnoreCase(".")){
				existe=true;
				break;
			}
			if(caracter.equalsIgnoreCase(",")){
				existe=true;
				break;
			}
			contador++;
		}
		String parteEntera 	= "";
		String parteDecimal = "";
		String parteDecimalDepurada = "";
		if(existe){
			parteEntera 	= entrada.substring(0,contador);
			parteDecimal = entrada.substring(contador+1,largo);
			if(parteDecimal.length()==0){
				parteDecimalDepurada = "00";
			}
			if(parteDecimal.length()==1){
				parteDecimalDepurada = parteDecimal+"0";
			}
			if(parteDecimal.length()>1){
				parteDecimalDepurada = parteDecimal.substring(0,2);
			}
		}else{
			parteEntera = entrada;
			parteDecimalDepurada = "00";
		}

		//System.out.println("posicion punto decimal:"+contador);	
		//System.out.println("parte entera:{"+parteEntera+"}");	
		//System.out.println("parte decimal:{"+parteDecimal+"}");	
		
		
		

		salida = parteEntera+parteDecimalDepurada;
		//System.out.println("parte decimal depurada:{"+parteEntera+parteDecimalDepurada+"}");	
		salida = relleno(salida,largoSalida,alineado,caracterRelleno);
		
		return salida;
	}
	
	public String relleno(String cadena,String largoIn,String alineado,String caracterRelleno){
		String 	salida 		= "";
		String 	charRelleno = "";
		int 	largo 		= Integer.valueOf(largoIn).intValue();
		int 	largoCompletar = largo - cadena.length();		
		if(caracterRelleno.equalsIgnoreCase("ES")) charRelleno=" ";
		if(caracterRelleno.equalsIgnoreCase("CE")) charRelleno="0";
		
		try {

			for(int i=0;i<largoCompletar;i++){
				if(alineado.equalsIgnoreCase(Constantes.IZQUIERDA)){
					cadena = cadena+charRelleno;
				}
				if(alineado.equalsIgnoreCase(Constantes.DERECHA)){
					cadena = charRelleno+cadena;
				}
			}
		} catch (Exception e) {
			for(int i=0;i<largo;i++){
				salida = salida+" ";
			}
		}
		salida = cadena;
		return salida;
	}
